// import logo from './logo.svg';
import './App.css';
import About from './components/About';
import Navbar from './components/Navbar';
import Textform from './components/Textform';
import React, { useState } from 'react';


function App() {

  const [mode, setMode] = useState('light'); // Initial mode is 'light'

  const toggleMode = () => {
    if (mode === 'light') {
      setMode('dark');
      document.body.style.backgroundColor = '#333'; // Set dark mode background
      document.body.style.color = 'white'; // Set text color for dark mode
    } else {
      setMode('light');
      document.body.style.backgroundColor = 'white'; // Set light mode background
      document.body.style.color = 'black'; // Set text color for light mode
    }
  };

  return (
    <>
   <Navbar title="My Website" home="Welcome to my homepage!" mode = {mode} togglemode = {togglemode} />

   <div className="container my-3">
  <Textform heading= "The great text app"  />
   </div>

   {/* <div className = "container my-3">
   <About/>
   </div> */}

    </>
  );
}

export default App;
