// import logo from './logo.svg';
import './App.css';
import About from './components/About';
import Navbar from './components/Navbar';
import Textform from './components/Textform';
import React, { useState } from 'react';
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link
} from "react-router-dom";


function App() {

  const [mode, setMode] = useState('light'); 

  const togglemode = () => {
    if (mode === 'light') {
      setMode('dark');
      document.title = 'Textutils- dark mode '
    } else {
      setMode('light');
      document.title = 'Textutils- light mode '
    }
  };

  return (
    <>
    <Route>
   <Navbar title="My Website" home="Welcome to my homepage!" mode = {mode} togglemode = {togglemode} />

   <Switch>
          <Route path="/about">
          <div className = "container my-3">
          <About/>
          </div>
          </Route>

          <Route path="/Tex">
            <Home />
          </Route>
        </Switch>



   <div className="container my-3">
  <Textform heading= "The great text app"   mode = {mode} />
   </div>

   <div className = "container my-3">
   <About/>
   </div>



   </Route>
    </>
  );
}

export default App;
